//实现三子棋//思路：//三子棋棋盘3*3，用二维数组表示棋盘//第一步：初始化棋盘//第二步：打印棋盘//第三步：玩家落子的坐标play_move,电脑落子的坐标computer_move//第四步：判断输赢//        玩家赢//        电脑赢//        平局void g_board(char board[ROW][COL], int row, int col); {	memset(board, ' ', row*col*sizeof(char));}void display_board(char board[ROW][COL], int row, int col); {	int i = 0;	for (i = 0; i < row; i++){		printf(" %c | %c |%c \n", board[i][0], board[i][1], board[i][2]);		if (i != 2)			printf(" --- | --- | --- \n");	}}void player_move(char board[ROW][COL], int row, int col); {	int x = 0, y = 0;	printf("玩家走:\n");	while (1){		printf("请输入坐标:\n");		scanf("%d%d", &x, &y);		if (x >= 1 && x <= row && y >= 1 && y <= col){			if (board[x - 1][y - 1] == ' '){				board[x - 1][y - 1] = 'x';				break;			}			else{				printf("坐标被占用\n");			}		}		else{			("坐标非法\n");		}	}}void computer_move(char board[ROW][COL], int row, int col); {	int x = 0, y = 0;	printf("电脑走:\n");	while (1){		x = rand() % row;		x = rand() % col;		if (board[x][y] == ' '){			board[x][y] == '0 ';			break;		}	}}static int check_full(char board[ROW][COL], int row, int col); {	for (int i = 0; i < row; i++)	{		for (int j = 0; j < col; j++)		{			if (board[i][j] != ' ')				return 0;		}	}	return 1;}char check_win(char board[ROW][COL], int row, int col){	for (int i = 0; i < row; i++)	{		if ((board[i][0] == board[i][1]) &&			(board[i][1] == board[i][2]) && board[i][1] != ' ')		{			return board[i][0];		}	}	for (int i = 0; i < col; i++)	{		if ((board[0][i] == board[1][i]) &&			(board[1][i] == board[2][i]) && board[1][i] != ' ')		{			return board[0][i];		}	}	if ((board[0][0] == board[1][1]) &&		(board[1][1] == board[2][2]) && board[1][1] != ' ')	{		return board[0][0];	}	if ((board[2][0] == board[1][1]) &&		(board[1][1] == board[0][2]) && board[1][1] != ' ')	{		return board[2][0];	}	if (check_full(board, row, col))	{		return "q";	}	return ' ';}#define _CRT_SECURE_NO_WARNINGS 1void menu(){	printf("**************************\n");	printf("***1.开始       0.结束****\n");	printf("**************************\n");}void game(){	char ret = 0;	char board[ROW][COL] = { 0 };	init_board(board, ROW, COL);	display_board(board, ROW, COL);	while (1)	{		player_move(board, ROW, COL);		ret = check_win(board, ROW, COL);		if (ret != ' ')			break;		display_board(board, ROW, COL);		computer_move(board, ROW, COL);		ret = check_win(board, ROW, COL);		if (ret != ' ')			break;		display_board(board, ROW, COL);	}	if (ret == 'X')	{		printf("玩家赢\n");	}	else if (ret == '0')	{		printf("电脑赢\n");	}	else if (ret == 'q')	{		printf("平局\n");	}}void test(){	int input = 0;	srand((unsigned)time(NULL));	do	{		menu();		printf("请选择：");		scanf("%d", &input);		switch (input)		{		case 1:			game();			break;		case 0:			printf("退出游戏\n");			break;		default:			printf("选择错误\n");			break;		}	} while (input);}int main(){	test();	return 0;}